import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import { userLoginAsync } from './../asyncActions/authAsyncActions';


function Login() {

    const authObj = useSelector(state => state.auth);
    const dispatch = useDispatch();

    const { userLoginLoading, loginError } = authObj;

    const username = useFormInput('');
    const password = useFormInput('');

    //handle button for login
    const handleLogin = () => {
        setError(null);
        setLoading(true);
        axios.post('http://localhost:4202/login', { email: username.value, password: password.value }, {withCredentials: true})
            .then(response => {
                response = response.data;

                setLoading(false);

                props.history.push('/dashboard');
            }).catch(error => {
                setLoading(false);
                if (error.response.status === 401) setError(error.response.data);
                else setError('Something went wrong. Please try again later');
            })
    }

    return (
        <div>
            Login<br /><br />
            <div>
                Username<br />
                <input type="text" {...username} autoComplete="new-password" />
            </div>
            <div style={{ marginTop: 10 }}>
                Password<br />
                <input type="password" {...password} autoComplete="new-password" />
            </div>
            {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
            <input type="button" value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} disabled={loading} /><br />
        </div>
    );
}

const useFormInput = initialValue => {
    const [value, setValue] = useState(initialValue);

    const handleChange = e => {
        setValue(e.target.value);
    }
    return {
        value,
        onChange: handleChange
    }
}

export default Login;
